<script src="https://cdnjs.cloudflare.com/ajax/libs/pdfmake/0.1.53/pdfmake.min.js"></script>

<script>
    $(document).ready(function() {
        $('#example').DataTable({
            dom: 'Bfrtip',
            buttons: [
                // 'copy', 'csv', 'excel', 'pdf', 'print',
                {
                    extend: 'copy',
                    exportOptions: {
                        'columns': ':visible:not(:last-child)'
                    }
                },
                {
                    extend: 'csv',
                    exportOptions: {
                        'columns': ':visible:not(:last-child)'
                    }
                },
                {
                    extend: 'excel',
                    exportOptions: {
                        'columns': ':visible:not(:last-child)'
                    }
                },
                {
                    // extend: 'pdf',
                    text: 'Export',
                    extend: 'pdfHtml5',
                    filename: 'custom_pdf',
                    orientation: 'portrait', //landscape
                    pageSize: 'A4', //A3 , A5 , A6 , legal , letter

                    exportOptions: {
                        'columns': ':visible:not(:last-child)'
                    },

                    customize: function(doc) {
                        //Remove the title created by datatTables
                        doc.content.splice(0, 1);
                        //Create a date string that we use in the footer. Format is dd-mm-yyyy
                        var now = new Date();
                        var jsDate = now.getDate() + '-' + (now.getMonth() + 1) + '-' + now
                            .getFullYear();
                        // var logo = 'data:image/jpeg;base64,';

                        doc.pageMargins = [20, 60, 20, 30];

                        // Set the font size fot the entire document
                        doc.defaultStyle.fontSize = 10;

                        // Set the fontsize for the table header
                        doc.styles.tableHeader.fontSize = 11;
                        doc.styles.tableHeader.margin = 5;
                        //doc.styles.tableHeader.background = '#228b22';

                        // Create a header object with 3 columns
                        // Left side: Logo
                        // Middle: brandname
                        // Right side: A document title
                        doc['header'] = (function() {
                            return {
                                columns: [{
                                        alignment: 'left',
                                        // italics: true,
                                        text: 'All Purchases',
                                        fontSize: 18,
                                        margin: [10, 0]
                                    },
                                    // {
                                    // 	alignment: 'left',
                                    // 	fontSize: 14,
                                    // 	text: 'All Purchases',
                                    // }
                                ],
                                margin: 20
                            }
                        });
                        // Create a footer object with 2 columns
                        // Left side: report creation date
                        // Right side: current page and total pages
                        doc['footer'] = (function(page, pages) {
                            return {
                                columns: [{
                                        alignment: 'left',
                                        text: ['Created on: ', {
                                            text: jsDate.toString()
                                        }]
                                    },
                                    {
                                        alignment: 'right',
                                        text: ['page ', {
                                            text: page.toString()
                                        }, ' of ', {
                                            text: pages.toString()
                                        }]
                                    }
                                ],
                                margin: 20
                            }
                        });
                        // Change dataTable layout (Table styling)
                        // To use predefined layouts uncomment the line below and comment the custom lines below
                        // doc.content[0].layout = 'lightHorizontalLines'; // noBorders , headerLineOnly
                        var objLayout = {};
                        objLayout['hLineWidth'] = function(i) {
                            return .5;
                        };
                        objLayout['vLineWidth'] = function(i) {
                            return .5;
                        };
                        objLayout['hLineColor'] = function(i) {
                            return '#aaa';
                        };
                        objLayout['vLineColor'] = function(i) {
                            return '#aaa';
                        };
                        objLayout['paddingLeft'] = function(i) {
                            return 0;
                        };
                        objLayout['paddingRight'] = function(i) {
                            return 0;
                        };
                        doc.content[0].layout = objLayout;
                    }

                },
                {
                    extend: 'print',
                    exportOptions: {
                        'columns': ':visible:not(:last-child)'
                    }
                },
            ],
            initComplete: function() {
                var btns = $('.dt-button');
                btns.addClass('btn btn-secondary btn-sm m-1');
                btns.removeClass('dt-button');
                var btn1 = $('.pagination').find('.paginate_button');
                btn1.removeClass('paginate_button');
            }
        });
    });
</script>
